import React ,{Component,useState} from "react";
import Head from "next/head";
import Button from '@material-ui/core/Button';
import { fade, makeStyles, Theme, createStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import clsx from 'clsx';
import Toolbar from '@material-ui/core/Toolbar';
import IconButton from '@material-ui/core/IconButton';
import Hidden from '@material-ui/core/Hidden';
import Typography from '@material-ui/core/Typography';
import InputBase from '@material-ui/core/InputBase';
import Drawer from '@material-ui/core/Drawer';
import Badge from '@material-ui/core/Badge';
import MenuItem from '@material-ui/core/MenuItem';
import Menu from '@material-ui/core/Menu';
import { withStyles } from '@material-ui/core/styles';
import MenuIcon from '@material-ui/icons/Menu';
import SearchIcon from '@material-ui/icons/Search';
import AccountCircle from '@material-ui/icons/AccountCircle';
import Divider from '@material-ui/core/Divider';
import MenuList from '@material-ui/core/MenuList';
import ListItem from '@material-ui/core/ListItem';
import MailIcon from '@material-ui/icons/Mail';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import InboxIcon from '@material-ui/icons/MoveToInbox';
import List from '@material-ui/core/List';
import NotificationsIcon from '@material-ui/icons/Notifications';
import MoreIcon from '@material-ui/icons/MoreVert';
import {inHomeComponentProps} from './inHomeComponentProps'
import {inHomeComponentState} from './inHomeComponentState'

import ChatComponent from "../../components/chat/chatComponent";
import {homeStyle} from './homeStyle'
import TextLogo from "../../layouts/textLogo";
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';
import ChevronRightIcon from '@material-ui/icons/ChevronRight';
class homeComponent extends Component<inHomeComponentProps, inHomeComponentState>  {
    constructor(props:inHomeComponentProps) {
        super(props)
        this.state = {
            menuId:'primary-search-account-menu',
            mobileMenuId : 'primary-search-account-menu-mobile',
            isMenuOpen:false,
            drawerOpen:false
           }
    }
      handleDrawerOpen = () => {
          this.setState({ drawerOpen: !this.state.drawerOpen })
    };

      handleDrawerClose = () => {
          this.setState({ drawerOpen: this.state.drawerOpen })
    };
    handleDrawerToggle = () => {
        this.setState({ isMenuOpen: !this.state.isMenuOpen })
    }

    render() {
        const {classes, theme} = this.props
        const renderMenu = (
            <Menu
                anchorOrigin={{ vertical: 'top', horizontal: 'right' }}
                id={this.state.menuId}
                keepMounted
                transformOrigin={{ vertical: 'top', horizontal: 'right' }}
                open={this.state.isMenuOpen}
                onClose={this.handleDrawerToggle}
           >
                <MenuItem  >Profile</MenuItem>
            </Menu>
        );

      const drawer =(

          <Drawer
              classes={{
                  paper: classes.drawerPaperLarge,
              }}
              variant="persistent"
              anchor="left"
              open={this.state.drawerOpen}
              ModalProps={{
                  keepMounted: true, // Better open performance on mobile.
              }}
          >
              <div className={classes.drawerHeader}>
                  <IconButton onClick={()=>this.handleDrawerClose()}>
                      {/*{theme.direction === 'ltr' ? <ChevronLeftIcon /> : <ChevronRightIcon />}*/}
                  </IconButton>
              </div>

              <List>
                  {['Home', 'Profile', 'People'].map((text, index) => (
                      <ListItem button key={text}>
                          <ListItemIcon>{index % 2 === 0 ? <InboxIcon /> : <MailIcon />}</ListItemIcon>
                          <ListItemText primary={text} />
                      </ListItem>
                  ))}
              </List>

          </Drawer>

      );

      const main = (

          <main   className={clsx(classes.content, {
              [classes.contentShift]: open,
          })}>

              {/*<ChatComponent > </ChatComponent>*/}
              <div className={classes.drawerHeader} />
              <Typography paragraph>
                  Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt
                  ut labore et dolore magna aliqua. Rhoncus dolor purus non enim praesent elementum
                  facilisis leo vel. Risus at ultrices mi tempus imperdiet. Semper risus in hendrerit
                  gravida rutrum quisque non tellus. Convallis convallis tellus id interdum velit laoreet id
                  donec ultrices. Odio morbi quis commodo odio aenean sed adipiscing. Amet nisl suscipit
                  adipiscing bibendum est ultricies integer quis. Cursus euismod quis viverra nibh cras.
                  Metus vulputate eu scelerisque felis imperdiet proin fermentum leo. Mauris commodo quis
                  imperdiet massa tincidunt. Cras tincidunt lobortis feugiat vivamus at augue. At augue eget
                  arcu dictum varius duis at consectetur lorem. Velit sed ullamcorper morbi tincidunt. Lorem
                  donec massa sapien faucibus et molestie ac.
              </Typography>
              <Typography paragraph>
                  Consequat mauris nunc congue nisi vitae suscipit. Fringilla est ullamcorper eget nulla
                  facilisi etiam dignissim diam. Pulvinar elementum integer enim neque volutpat ac
                  tincidunt. Ornare suspendisse sed nisi lacus sed viverra tellus. Purus sit amet volutpat
                  consequat mauris. Elementum eu facilisis sed odio morbi. Euismod lacinia at quis risus sed
                  vulputate odio. Morbi tincidunt ornare massa eget egestas purus viverra accumsan in. In
                  hendrerit gravida rutrum quisque non tellus orci ac. Pellentesque nec nam aliquam sem et
                  tortor. Habitant morbi tristique senectus et. Adipiscing elit duis tristique sollicitudin
                  nibh sit. Ornare aenean euismod elementum nisi quis eleifend. Commodo viverra maecenas
                  accumsan lacus vel facilisis. Nulla posuere sollicitudin aliquam ultrices sagittis orci a.
              </Typography>
          </main>


      );




        return (
           <div>

                <AppBar position="static" className={classes.header}  >
                    <Toolbar>
                        <IconButton
                            edge="start"
                            className=''
                            onClick={()=>this.handleDrawerOpen()}
                            color="inherit"
                            aria-label="open drawer"
                        >
                            <MenuIcon />
                        </IconButton>
                        <div className={classes.drawerHeader} >
                            <TextLogo viewBox='0 00 100 60' className={classes.logo}/>
                        </div>
                        <MenuList style={{ color: 'rgb(117, 117, 117)', width: '113px', paddingTop: '0px' }}>
                            <div className={classes.homeTitle}>

                                Home
                            </div>

                        </MenuList>
                        <div className={classes.search}>
                            <div className={classes.searchIcon}>
                                <SearchIcon />
                            </div>
                            <InputBase
                                placeholder="Find your favorite posts and people ..."
                                classes={{
                                    root: classes.inputRoot,
                                    input: classes.inputInput,
                                }}
                                inputProps={{ 'aria-label': 'search' }}
                            />
                        </div>
                        <div className={classes.grow} />
                        <div className={classes.sectionDesktop}>

                            <IconButton aria-label="show 17 new notifications" color="inherit">
                                <Badge badgeContent={17} color="secondary">
                                    <NotificationsIcon />
                                </Badge>
                            </IconButton>
                            <IconButton
                                edge="end"
                                aria-label="account of current user"
                                aria-controls={this.state.menuId}
                                aria-haspopup="true"

                                color="inherit"
                            >
                                <AccountCircle />
                            </IconButton>
                        </div>
                        <div className={classes.sectionMobile}>
                            <IconButton
                                aria-label="show more"

                                aria-haspopup="true"

                                color="inherit"
                            >
                                <MoreIcon />
                            </IconButton>
                        </div>
                    </Toolbar>
                </AppBar>


               {drawer}
               {main}

               {renderMenu}


            </div>

        )

    }

}

export default withStyles(homeStyle as any)(homeComponent)
