import React ,{Component,useState} from "react";
import Button from '@material-ui/core/Button';
import { fade, makeStyles, Theme, createStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import InputBase from '@material-ui/core/InputBase';
import Badge from '@material-ui/core/Badge';
import MenuItem from '@material-ui/core/MenuItem';
import Menu from '@material-ui/core/Menu';
import MenuIcon from '@material-ui/icons/Menu';
import SearchIcon from '@material-ui/icons/Search';
import AccountCircle from '@material-ui/icons/AccountCircle';
import MailIcon from '@material-ui/icons/Mail';
import NotificationsIcon from '@material-ui/icons/Notifications';
import MoreIcon from '@material-ui/icons/MoreVert';
import {inHomeComponentProps} from './inHomeComponentProps'
import {inHomeComponentState} from './inHomeComponentState'
// import {homeStyle} from './homeStyle'
const homeStyle = makeStyles((theme) => ({
    grow: {
        flexGrow: 1,
    },
    menuButton: {
        marginRight: theme.spacing(2),
    },
    title: {
        display: 'none',
        [theme.breakpoints.up('sm')]: {
            display: 'block',
        },
    },
    search: {
        position: 'relative',
        borderRadius: theme.shape.borderRadius,
        backgroundColor: fade(theme.palette.common.white, 0.15),
        '&:hover': {
            backgroundColor: fade(theme.palette.common.white, 0.25),
        },
        marginRight: theme.spacing(2),
        marginLeft: 0,
        width: '100%',
        [theme.breakpoints.up('sm')]: {
            marginLeft: theme.spacing(3),
            width: 'auto',
        },
    },
    searchIcon: {
        padding: theme.spacing(0, 2),
        height: '100%',
        position: 'absolute',
        pointerEvents: 'none',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
    },
    inputRoot: {
        color: 'inherit',
    },
    inputInput: {
        padding: theme.spacing(1, 1, 1, 0),
        // vertical padding + font size from searchIcon
        paddingLeft: `calc(1em + ${theme.spacing(4)}px)`,
        transition: theme.transitions.create('width'),
        width: '100%',
        [theme.breakpoints.up('md')]: {
            width: '20ch',
        },
    },
    sectionDesktop: {
        display: 'none',
        [theme.breakpoints.up('md')]: {
            display: 'flex',
        },
    },
    sectionMobile: {
        display: 'flex',
        [theme.breakpoints.up('md')]: {
            display: 'none',
        },
    },
}));
class homeComponent extends Component<inHomeComponentProps, inHomeComponentState>  {
    constructor(props:inHomeComponentProps) {
        super(props)
        this.state = {
            menuId:'primary-search-account-menu',
            mobileMenuId : 'primary-search-account-menu-mobile',
            isMenuOpen:false,

           }


    }
    handleDrawerToggle = () => {
        this.setState({ isMenuOpen: !this.state.isMenuOpen })
    }

    render() {


        const renderMenu = (
            <Menu
                anchorOrigin={{ vertical: 'top', horizontal: 'right' }}
                id={this.state.menuId}
                keepMounted
                transformOrigin={{ vertical: 'top', horizontal: 'right' }}
                open={this.state.isMenuOpen}
                onClose={this.handleDrawerToggle}
           >
                <MenuItem  >Profile</MenuItem>
            </Menu>
        );
        return (
           <div>

               {classes.search}
                {/*<AppBar position="static">*/}
                {/*    <Toolbar>*/}
                {/*        <IconButton*/}
                {/*            edge="start"*/}
                {/*            className=''*/}
                {/*            color="inherit"*/}
                {/*            aria-label="open drawer"*/}
                {/*        >*/}
                {/*            <MenuIcon />*/}
                {/*        </IconButton>*/}
                {/*        <Typography   variant="h6" noWrap>*/}
                {/*            Material-UI*/}
                {/*        </Typography>*/}
                {/*        <div className={classes.search}>*/}
                {/*            <div className={homeStyle.searchIcon}>*/}
                {/*                <SearchIcon />*/}
                {/*            </div>*/}
                {/*            <InputBase*/}
                {/*                placeholder="Search…"*/}
                {/*                classes={{*/}
                {/*                    root: homeStyle.inputRoot,*/}
                {/*                    input: homeStyle.inputInput,*/}
                {/*                }}*/}
                {/*                inputProps={{ 'aria-label': 'search' }}*/}
                {/*            />*/}
                {/*        </div>*/}
                {/*        <div className={homeStyle.grow} />*/}
                {/*        <div className={homeStyle.sectionDesktop}>*/}

                {/*            <IconButton aria-label="show 17 new notifications" color="inherit">*/}
                {/*                <Badge badgeContent={17} color="secondary">*/}
                {/*                    <NotificationsIcon />*/}
                {/*                </Badge>*/}
                {/*            </IconButton>*/}
                {/*            <IconButton*/}
                {/*                edge="end"*/}
                {/*                aria-label="account of current user"*/}
                {/*                aria-controls={this.state.menuId}*/}
                {/*                aria-haspopup="true"*/}

                {/*                color="inherit"*/}
                {/*            >*/}
                {/*                <AccountCircle />*/}
                {/*            </IconButton>*/}
                {/*        </div>*/}
                {/*        <div className={homeStyle.sectionMobile}>*/}
                {/*            <IconButton*/}
                {/*                aria-label="show more"*/}

                {/*                aria-haspopup="true"*/}

                {/*                color="inherit"*/}
                {/*            >*/}
                {/*                <MoreIcon />*/}
                {/*            </IconButton>*/}
                {/*        </div>*/}
                {/*    </Toolbar>*/}
                {/*</AppBar>*/}

               {renderMenu}

            </div>

        )

    }

}

export default homeComponent
