    divider: {
        margin: `${theme.spacing.unit }px 0`
    },
    '.MuiCardHeader-subheader':{
        color:'#000'
    },
    typographyHeading:{
        width: '100%',
        textAlign: 'center',
        marginTop:'-29px',
        background: '#E4E6E9',
        padding: '10px' ,
        borderRadius:' 100px',
        marginLeft: '50px'
    },
    addVideo:{

        background:'transparent !important',
        boxShadow:'none !important'
    },
    addPhoto:{
        background:'transparent !important',
        boxShadow:'none !important'
    },
    input:{
        display:'none'
    },

})
