import React ,{Component,useState} from "react";
import FormControl from '@material-ui/core/FormControl';
import Grid from '@material-ui/core/Grid';
import IconButton from '@material-ui/core/IconButton';
import Input from '@material-ui/core/Input';
import InputLabel from '@material-ui/core/InputLabel';
import Button from '@material-ui/core/Button';
import InputAdornment from '@material-ui/core/InputAdornment';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemSecondaryAction from '@material-ui/core/ListItemSecondaryAction';
import ListItemText from '@material-ui/core/ListItemText';
import MenuItem from '@material-ui/core/MenuItem';
import Popover from '@material-ui/core/Popover';
import { withStyles } from '@material-ui/core/styles';
import withWidth, { isWidthDown } from '@material-ui/core/withWidth';
import BackIcon from '@material-ui/icons/ArrowBack';
import MenuIcon from '@material-ui/icons/Menu';
import MoreIcon from '@material-ui/icons/MoreVert';
import SearchIcon from '@material-ui/icons/Search';
import SendIcon from '@material-ui/icons/Send';
import EmojiIcon from '@material-ui/icons/SentimentSatisfied';
import classNames from 'classnames';

import clsx from 'clsx';

import CardMedia from '@material-ui/core/CardMedia';

import CardActions from '@material-ui/core/CardActions';
import Collapse from '@material-ui/core/Collapse';

import { red } from '@material-ui/core/colors';
import FavoriteIcon from '@material-ui/icons/Favorite';
import ShareIcon from '@material-ui/icons/Share';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import MoreVertIcon from '@material-ui/icons/MoreVert';

import {ChatProps} from './chatProps';
import Faker from 'faker';
import {ChatState} from './chatState';
import Card from "@material-ui/core/Card";
import  {chatStyles} from './chatStyles'
import Typography from '@material-ui/core/Typography';
import CardHeader from '@material-ui/core/CardHeader';
import {HomeHeaderProps} from "../homeHeader/homeHeaderProps";
import Divider from "@material-ui/core/Divider";
import CardContent from "@material-ui/core/CardContent";
import Avatar from "@material-ui/core/Avatar";
class ChatComponent extends Component<ChatProps,ChatState>{
    constructor(props:ChatProps) {
        super(props)

        this.state = {
            expanded:null,
            activeIndex: null
        }
    }

    handleExpandClick = (index , e) => {

        this.setState({ expanded:  this.state.expanded === index ? null :index})
        }
    render(){
        let content;
        const {classes, theme} = this.props;

        const posts = [
            {
                id:"1",
                avatar: Faker.internet.avatar(),
                userName : Faker.internet.userName()

            },
            {
                id:"2",
                avatar: Faker.internet.avatar(),
                userName : Faker.internet.userName()
            },
            {
                id:"3",
                avatar: Faker.internet.avatar(),
                userName : Faker.internet.userName()
            }

            ];

        const mainChat = (

            content = posts.map((post,index) => {

            return (

                <Card className={classes.postContent} key={index} >
                    <CardHeader
                        avatar={
                            <Avatar aria-label="recipe"
                                    src={
                                        post.avatar
                                    }

                                    className={classes.avatar}>
                                R
                            </Avatar>
                        }
                        action={
                            <IconButton aria-label="settings">
                                <MoreVertIcon />
                            </IconButton>
                        }
                        title={post.userName}
                        subheader="September 14, 2016"
                    />
                    <CardMedia
                        className={classes.media}
                        image={Faker.image.imageUrl()}
                        title="Paella dish"
                    />
                    <CardContent>
                        <Typography variant="body2" color="textSecondary" component="p">
                            This impressive paella is a perfect party dish and a fun meal to cook together with your
                            guests. Add 1 cup of frozen peas along with the mussels, if you like.
                        </Typography>
                    </CardContent>
                    <CardActions disableSpacing>
                        <IconButton aria-label="add to favorites">
                            <FavoriteIcon />
                        </IconButton>
                        <IconButton aria-label="share">
                            <ShareIcon />
                        </IconButton>
                        <IconButton
                            className={clsx(classes.expand, {
                                [classes.expandOpen]: this.state.expanded,
                            })}
                            onClick={this.handleExpandClick.bind(this, index)}
                            aria-expanded={this.state.expanded}
                            aria-label="show more"
                        >
                            <ExpandMoreIcon />
                        </IconButton>
                    </CardActions>
                    <Collapse in={this.state.expanded === index} timeout="auto" unmountOnExit>
                        <CardContent>

                            <FormControl fullWidth className={classes.margin} variant="outlined">
                                <InputLabel  >Add Comment</InputLabel>
                            <Input
                                id="standard-adornment-weight"



                                aria-describedby="standard-weight-helper-text"
                                inputProps={{
                                    'aria-label': 'weight',
                                }}
                            />
                                <Button variant="outlined">Default</Button>
                            </FormControl>
                        </CardContent>
                    </Collapse>
                </Card>


            )
         }


        ))

        return(
            <div>


                <Card className={classes.card}>


                    <CardHeader

                        subheader="Create Post"

                    >



                    </CardHeader>
                    <Divider variant="inset" className={classes.divider} component="li" />


                    <CardContent className={classes.context}>

                        <Avatar
                            className={classes.userImage}
                            src={
                                Faker.internet.avatar()
                            }
                        />
                        <Typography
                            className={classes.typographyHeading}
                            variant={"subtitle2"}
                            gutterBottom
                        >
                            Whats new with you ?
                        </Typography>

                    </CardContent>


                </Card>


                {mainChat}


            </div>
        )

    }

}
export default withStyles(chatStyles as any)(ChatComponent)
