import React ,{Component,useState} from "react";
import {HomeHeaderProps} from "./homeHeaderProps";
import {HomeHeaderState} from "./homeHeaderState";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import IconButton from "@material-ui/core/IconButton";
import MenuIcon from "@material-ui/icons/Menu";
import TextLogo from "../../layouts/textLogo";
import MenuList from "@material-ui/core/MenuList";
import SearchIcon from "@material-ui/icons/Search";
import InputBase from "@material-ui/core/InputBase";
import Badge from "@material-ui/core/Badge";
import NotificationsIcon from "@material-ui/icons/Notifications";
import AccountCircle from "@material-ui/icons/AccountCircle";
import MoreIcon from "@material-ui/icons/MoreVert";
import {inHomeComponentProps} from "../../containers/home/inHomeComponentProps";
import {withStyles} from "@material-ui/core/styles";
import Popover from "@material-ui/core/Popover";
import Box from "@material-ui/core/Box";
import Typography from "@material-ui/core/Typography";
import Card from "@material-ui/core/Card";
import Divider from "@material-ui/core/Divider";
import CardContent from "@material-ui/core/CardContent";
import SearchBox from "../searchBox/searchBoxComponent";
import { fade, makeStyles, Theme, createStyles } from '@material-ui/core/styles';
const drawerWidth = 220
 const homeHeaderStyle = (theme: any) => ({
    root: {
        width: '100%',
        height: '100%',
        marginTop: theme.spacing(3),
        zIndex: 1,
        overflow: 'hidden',
    },
    appFrame: {
        position: 'relative',
        display: 'flex',
        width: '100%',
        height: '100%',
        // overflowY: 'auto'
    },
    navIconHide: {
        [theme.breakpoints.up('md')]: {
            display: 'none',
        },
    },
    grow: {
        flexGrow: 1,
    },
    menuButton: {
        marginRight: theme.spacing(2),
    },
    title: {
        color:'green',
        display: 'none',
        [theme.breakpoints.up('sm')]: {
            display: 'block',
        },
    },
    search: {
        position: 'relative',
        padding: '3px',
        width: '704px !important',
        borderRadius: theme.shape.borderRadius,
        backgroundColor: fade(theme.palette.common.white, 0.15),
        '&:hover': {
            backgroundColor: fade(theme.palette.common.white, 0.25),
        },
        marginRight: theme.spacing(2),
        marginLeft: 0,

        [theme.breakpoints.up('sm')]: {
            marginLeft: '0px',
            width: 'auto',
        },
    },
    searchIcon: {
        padding: theme.spacing(0, 2),
        height: '100%',
        position: 'absolute',
        pointerEvents: 'none',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
    },
    inputRoot: {
        color: 'inherit',
    },
    inputInput: {
        padding: theme.spacing(1, 1, 1, 0),
        // vertical padding + font size from searchIcon
        paddingLeft: `calc(1em + ${theme.spacing(4)}px)`,
        transition: theme.transitions.create('width'),
        width: '100%',
        [theme.breakpoints.up('md')]: {
            width: '20ch',
        },
    },
    sectionDesktop: {
        display: 'none',
        [theme.breakpoints.up('md')]: {
            display: 'flex',
        },
    },
    sectionMobile: {
        display: 'flex',
        [theme.breakpoints.up('md')]: {
            display: 'none',
        },
    },
    palette: {
        backgroundColor:'red !important'
    },

    drawerHeader: {
        //  backgroundColor: theme.palette.secondary.main,
        padding: 10,
        textAlign: 'center',
        marginLeft:'97px',
        [theme.breakpoints.down('xs')]: {
            marginLeft:0,
        },
    },
    drawerPaper: {
        maxWidth: drawerWidth,
        marginTop: '70px',
        width: drawerWidth,
        [theme.breakpoints.up('md')]: {
            width: drawerWidth,
            position: 'relative',
            height: '100%',
        },
    },
    divider: {
        margin: `${theme.spacing.unit * 3}px 0`
    },
    drawerPaperLarge: {
        width: drawerWidth,
        [theme.breakpoints.up('md')]: {
            width: drawerWidth,
            height: '100%',
        },
        top: 70,
        backgroundColor: '#fafafa',
        borderRight: 0
    },
    menu: {
        height: '100%',
    },
    content: {
        backgroundColor: 'transparent',
        width: '100%',
        flexGrow: 1,
        padding: theme.spacing(3),
        paddingTop: 15,
        transition: theme.transitions.create('margin', {
            easing: theme.transitions.easing.sharp,
            duration: theme.transitions.duration.leavingScreen,
        }),
        height: 'calc(100% - 56px)',
        marginTop: 44,
        marginLeft:drawerWidth,
        [theme.breakpoints.up('sm')]: {
            height: 'calc(100% - 64px)',
            marginTop: 44,
            paddingTop: 15,
            marginLeft:drawerWidth
        },
        [theme.breakpoints.down('sm')]: {
            height: 'calc(100% - 64px)',
            marginTop: 44,
            paddingTop: 15,
            marginLeft:drawerWidth
        },
    },
    'MuiAppBar-colorPrimary' : {
        backgroundColor: '#b50d1a !important',
    },
    'content-left': {
        marginLeft: 0,
    },
    'content-right': {
        marginRight: 0,
    },
    contentShift: {
        transition: theme.transitions.create('margin', {
            easing: theme.transitions.easing.easeOut,
            duration: theme.transitions.duration.enteringScreen,
        }),
    },
    'contentShift-left': {
        marginLeft: 0,
        [theme.breakpoints.up('md')]: {
            marginLeft: drawerWidth
        }
    },
    'contentShift-right': {
        marginRight: 0,
        [theme.breakpoints.up('md')]: {
            marginRight: drawerWidth
        }
    },
    logo: {
        fill: 'currentColor',
        width: '100%',
        height: '2em',
        display: 'inline-block',
        fontSize: '17px',
        transition: 'fill 200ms cubic-bezier(0.4, 0, 0.2, 1) 0ms',
        userSelect: 'none',
        flexShrink: 0
    },
    header : {
        background:'#5b5958',
        position: 'fixed',
        right: '0',
        top: '0',
    },

    homeTitle:{
        'color': '#fff',
        'margin-top': '5px',
        'left': '-9%',
        'border-left-color': '#fff',
        'border-left': '1px solid #fff',
        'padding-left': '24px',
    },
    'MuiPaper-root':{
        'borderRight':'0px !important'
    },
    'MuiDivider-root':{
        'backgroundColor':'#fff !important'
    }


})
class HomeHeader extends Component<HomeHeaderProps,HomeHeaderState>{
    constructor(props:HomeHeaderProps) {
        super(props)
        this.state = {
            menuId:'primary-search-account-menu',
            mobileMenuId : 'primary-search-account-menu-mobile',
            isMenuOpen:false,
            drawerOpen:false,
            anchorEl: null,
        }
    }
    handleDrawerToggle = () => {
        this.setState({ isMenuOpen: !this.state.isMenuOpen })
    }
    onToggleSidebar = () => {
        const { onToggleDrawer } = this.props
        onToggleDrawer()
    }
    handleDrawerOpen = () => {
        this.setState({ drawerOpen: !this.state.drawerOpen })
    };

    handleAccountOpen = event => {

        this.setState({ anchorEl: event.currentTarget });

    }
    handleClose = (message) => () => {
        this.setState({ anchorEl: null });
        console.log(message);
    };
    handleDrawerClose = () => {
        this.setState({ drawerOpen: this.state.drawerOpen })
    };
            render(){
                const {classes, theme} = this.props
                const { anchorEl } = this.state;

                const popoverAccount = (

                    <Popover
                        anchorEl={anchorEl}
                        id="fade-menu"
                        open={Boolean(anchorEl)}
                        onClose={this.handleClose(null)}
                        style={{left:-50, top:50 }}
                        anchorOrigin={{ vertical: 'top', horizontal: 'center' }}
                        transformOrigin={{
                            vertical: 'center',
                            horizontal: 'center',
                        }}

                    >

                        <Box p={1} style={{cursor:"pointer"}}>
                            <Typography>Edit</Typography>
                        </Box>
                        <Box p={1}   style={{cursor:"pointer"}}>
                            <Typography>Delete</Typography>
                        </Box>
                        <Box p={1} style={{cursor:"pointer"}}>
                            <Typography>Disable Comments</Typography>
                        </Box>
                        <Box p={1} style={{cursor:"pointer"}}>
                            <Typography>Disable Sharing</Typography>
                        </Box>

                    </Popover>

                )
                return (

                    <div>

                        <AppBar position="static" className={classes.header}  >
                            <Toolbar>
                                {/*<IconButton*/}
                                {/*    edge="start"*/}
                                {/*    className=''*/}
                                {/*    onClick={()=>this.onToggleSidebar()}*/}
                                {/*    color="inherit"*/}
                                {/*    aria-label="open drawer"*/}
                                {/*>*/}
                                {/*    <MenuIcon />*/}
                                {/*</IconButton>*/}
                                <div className={classes.drawerHeader} >
                                    <TextLogo viewBox='0 00 100 60' className={classes.logo}/>
                                </div>
                                <MenuList style={{ color: 'rgb(117, 117, 117)', width: '113px', paddingTop: '0px' }}>
                                    <div className={classes.homeTitle}>

                                        Home
                                    </div>

                                </MenuList>
                               <SearchBox/>
                                <div className={classes.grow} />
                                <div className={classes.sectionDesktop}  style={{marginRight:56}}>

                                    <IconButton aria-label="show 17 new notifications" color="inherit" style={{marginRight:36}}>
                                        <Badge badgeContent={17} color="secondary">
                                            <NotificationsIcon />
                                        </Badge>
                                    </IconButton>
                                    <IconButton
                                        edge="end"
                                        aria-label="account of current user"
                                        aria-controls={this.state.menuId}
                                        aria-haspopup="true"
                                        onClick={this.handleAccountOpen}
                                        color="inherit"
                                    >
                                        <AccountCircle />
                                    </IconButton>


                                    {popoverAccount}

                                </div>
                                <div className={classes.sectionMobile}>
                                    <IconButton
                                        aria-label="show more"

                                        aria-haspopup="true"

                                        color="inherit"
                                    >
                                        <MoreIcon />
                                    </IconButton>
                                </div>
                            </Toolbar>
                        </AppBar>

                    </div>
