export const CreatePostStyle = (theme: any) => ({

    divider: {
        margin: `${theme.spacing.unit }px 0`
    },
    '.MuiCardHeader-subheader':{
        color:'#000'
    },
    typographyHeading:{
        width: '100%',
        textAlign: 'center',
        marginTop:'-29px'
    },
    addVideo:{

        background:'transparent !important',
        boxShadow:'none !important'
    },
    addPhoto:{
        background:'transparent !important',
        boxShadow:'none !important'
    },
    input:{
        display:'none'
    },

})
