import React, { Component, useState } from "react";
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import MenuItem from '@material-ui/core/MenuItem';
import Menu from '@material-ui/core/Menu';
import { withStyles } from '@material-ui/core/styles';
import Divider from '@material-ui/core/Divider';
import Faker from 'faker'
import Card from '@material-ui/core/Card';
import Link from '@material-ui/core/Link';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import { inHomeComponentProps } from './inHomeComponentProps'
import { inHomeComponentState } from './inHomeComponentState'
import HomeHeader from "../../components/homeHeader/homeHeader";
import ChatComponent from "../../components/chat/chatComponent";
import CreatePost from "../../components/createPost/createPostComponent";
import Cover from "../../components/cover/coverComponent";
import CardHeader from '@material-ui/core/CardHeader';
import { homeStyle } from './homeStyle'
import Avatar from "@material-ui/core/Avatar";
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import PublicIcon from '@material-ui/icons/Public';
import VisibilityIcon from '@material-ui/icons/Visibility';
import PeopleAltIcon from '@material-ui/icons/PeopleAlt';
import ProfileTabComponent from '../../components/profileTab/profileTabComponent'
import ProfileInfoComponent from '../../components/profileInfo/profileInfoComponent'
class homeComponent extends Component<inHomeComponentProps, inHomeComponentState>  {
    constructor(props: inHomeComponentProps) {
        super(props)
        this.state = {
            menuId: 'primary-search-account-menu',
            mobileMenuId: 'primary-search-account-menu-mobile',
            isMenuOpen: false,
            drawerOpen: false
        }

    }
    handleDrawerOpen = () => {
        this.setState({ drawerOpen: !this.state.drawerOpen })
    };
    handleDrawerClose = () => {
        this.setState({ drawerOpen: this.state.drawerOpen })
    };
    handleDrawerToggle = () => {
        this.setState({ isMenuOpen: !this.state.isMenuOpen })
    }
    render() {
        const { classes, theme, posts } = this.props
        const renderMenu = (
            <Menu
                anchorOrigin={{ vertical: 'top', horizontal: 'right' }}
                id={this.state.menuId}
                keepMounted
                transformOrigin={{ vertical: 'top', horizontal: 'right' }}
                open={this.state.isMenuOpen}
                onClose={this.handleDrawerToggle}
            >
                <MenuItem  >Profile</MenuItem>
            </Menu>
        );
        const userInfo = (
            <Card className={classes.card}>

                <Typography variant="body2" color="textSecondary" component="h2" style={{
                    fontSize: '1.0625rem',
                    padding: '14px', fontWeight: 'bold'
                }} >
                    About
                </Typography>

                <Typography variant="body2" color="textSecondary" component="h3" style={{
                    marginTop: '14px', padding: '14px'
                }} >
                    This group is for everyone who likes mountains, hiking, nature and outdoors. The beauty of mountains revealed by pictures and videos ... share your pictures and have a good time!
                </Typography>

                <List
                    component="nav"
                    aria-labelledby="nested-list-subheader"
                >
                    <ListItem >
                        <ListItemIcon style={{ minWidth: '34px', marginTop: '-19px' }}>
                            <PublicIcon style={{ fontSize: '21px' }} />
                        </ListItemIcon>
                        <ListItemText primary="Public" secondary="Anyone can see who's in the group and what they post." />
                    </ListItem>
                    <ListItem >
                        <ListItemIcon style={{ minWidth: '34px', marginTop: '-19px' }}>
                            <VisibilityIcon style={{ fontSize: '21px' }} />
                        </ListItemIcon>
                        <ListItemText primary="Visible" secondary="Anyone can find this group." />
                    </ListItem>
                    <ListItem >
                        <ListItemIcon style={{ minWidth: '34px' }}>
                            <PeopleAltIcon style={{ fontSize: '21px' }} />
                        </ListItemIcon>
                        <ListItemText primary="General" />
                    </ListItem>

                </List>
            </Card>

        );

        // const peopleList = (

        //     <Card className={classes.card}>

        //         <Typography
        //             className={classes.peopleHeader}
        //             variant={"h6"}
        //         >
        //             Peoples
        //             <Divider light className={classes.divider} />


        //         </Typography>
        //         <List className={classes.rootPeople}>

        //             {
        //                 this.props.users.map(function(item,i){

        //               return (
        //                       <div key={i}>
        //                           <ListItem alignItems="flex-start">
        //                               <ListItemAvatar>
        //                                   <Avatar alt="Remy Sharp" src={Faker.internet.avatar()} />
        //                               </ListItemAvatar>
        //                               <ListItemText
        //                                   secondary={
        //                                       <React.Fragment>
        //                                           <Typography
        //                                               component="span"
        //                                               variant="body2"
        //                                               className={classes.peopleUserName}
        //                                               color="textPrimary"
        //                                           >
        //                                               {item.username}
        //                                           </Typography>

        //                                       </React.Fragment>
        //                                   }
        //                               />
        //                           </ListItem>
        //                           <Divider variant="inset" className={classes.divider} component="li" />


        //                       </div>

        //                   )


        //                 })
        //             }

        //         </List>
        //     </Card>

        // );
        // const drawer =(

        //     <Drawer
        //         classes={{
        //             paper: classes.drawerPaperLarge,
        //         }}
        //         variant="persistent"
        //         anchor="left"
        //         open={this.state.drawerOpen}
        //         ModalProps={{
        //             keepMounted: true, // Better open performance on mobile.
        //         }}
        //     >
        //         <div className={classes.drawerHeader}>
        //             <IconButton onClick={()=>this.handleDrawerClose()}>
        //                 {/*{theme.direction === 'ltr' ? <ChevronLeftIcon /> : <ChevronRightIcon />}*/}
        //             </IconButton>
        //         </div>

        //         <List>
        //             {['Home', 'Profile', 'People'].map((text, index) => (
        //                 <ListItem button key={text}>
        //                     <ListItemIcon>{index % 2 === 0 ? <InboxIcon /> : <MailIcon />}</ListItemIcon>
        //                     <ListItemText primary={text} />
        //                 </ListItem>
        //             ))}
        //         </List>

        //     </Drawer>

        // );

        const cover = (
            <main className={classes.content}>
                <Cover />
                <ProfileInfoComponent />
                <hr style={{ width: '65%', color: '#eee' }} />
                <ProfileTabComponent />
            </main>
        );
        const profileInfo = (
            <main className={classes.content}>

            </main>
        );
        return (
            <div className={classes.root}>
                <HomeHeader onToggleDrawer={this.handleDrawerOpen} />
                <Grid container style={{ paddingTop: '60px !important' }} spacing={3}>
                    <Grid item xs={12} sm={12} lg={12} style={{ background: '#fff', marginTop: '-45' }}>
                        {cover}
                    </Grid>
                </Grid>
                <Grid container style={{ paddingTop: '60px !important', display: 'flex', justifyContent: 'center' }} spacing={3}>
                    <Grid item xs={12} sm={12} md={5} lg={5} className='profileInfo' >
                        <CreatePost />
                        <ChatComponent />
                    </Grid>

                    <Grid item xs={12} sm={12} md={3} lg={3} className='userInfo' >
                        {userInfo}

                    </Grid>

                </Grid>
            </div>

        )

    }

}

export default withStyles(homeStyle as any)(homeComponent)
